window.onload=function(){let tab=document.querySelectorAll(".tabs-item");let tabBody=document.querySelectorAll(".tabs-item-body");let index=0;for(let i=0;i<tab.length;i++)
{i!=0?tabBody[i].style.display='none':tabBody[i].style.display='block';tab[i].addEventListener("click",function(){if(this.className.indexOf("tab-active")==-1)
{this.className="tabs-item tab-active";tabBody[i].style.display='block';tabBody[index].style.display='none';tab[index].classList.remove("tab-active");tab[index].className="tabs-item tab-desactive";index=i;}});}}